const updateCart=n=>{fetch(`http://localhost:51421/api/cart/${n}`).then(n=>n.json()).then(n=>document.querySelector("span.badge").innerHTML=n)};window.onload=()=>{const n=document.cookie.match(/guid=([a-zA-Z0-9-]*)/)[1];try{document.querySelector("a.text-danger").addEventListener("click",n=>{window.confirm("Are you sure?")||n.preventDefault()})}catch(t){}try{document.querySelectorAll("input.amount").forEach(t=>{t.addEventListener("change",t=>{if(t.target.value<=0){if(window.confirm("You're settng the value to less than 1, would you like to romove it?"))return;t.target.value=1}let i=new FormData;i.append("guid",n);i.append("Id",t.target.dataset.product);i.append("Amount",t.target.value);let r=new Request("http://localhost:51421/api/cart/Update",{method:"POST",body:i});fetch(r).then(n=>n.json()).then(t=>{t=="200"&&console.log("IT WORKS!"),updateCart(n)}).catch(n=>console.log(n))})})}catch(t){console.log(t)}try{document.querySelectorAll()}catch(t){}updateCart(n)};